import React,{useRef,useState} from "react";
import PropTypes from 'prop-types';
import '../../styles/DropFileInput.css';
import { ImageConfig } from "../../configuration/ImageConfig";
// import imgSize from "./ImageSize";
import uploadImage from '../../assets/cloud-upload.png'

const DropFileInput = (props) => {

    const [fileList, setFileList] = useState([]);
    const onFileDrop = (e)=>{
        const newFile = e.target.files[0];
        let height = 0;
        let width = 0;
        if (/\.(jpe?g)$/i.test(newFile.name)) {
            const reader = new FileReader();
            reader.addEventListener("load", function () {
                const image = new Image();
                image.src = String(this.result);
                image.addEventListener('load', function () {
                    height=this.height;
                    width=this.width;
                });
                        
            }, false);
                    
            reader.readAsDataURL(newFile);
        }
        console.log(width, height)
        if (newFile.name.slice(-3).toLowerCase() =='jpg' && newFile.size <= 50000){
            const updatedList = [...fileList, newFile];
            setFileList(updatedList);
            props.onFileChange(updatedList);

        }
        else{
            alert('Not pass\nPlease make sure that your file size is\n1.) .jpg form\n2.) file size is not larger than 50KB')
        }
    }
    const fileRemove = (f)=>{
        const updatedList = [...fileList]
        updatedList.splice(fileList.indexOf(f), 1);
        setFileList(updatedList);
        props.onFileChange(updatedList);
    }

    const wrapperRef = useRef(null)
    const onDragEnter = ()=>{
        wrapperRef.current.classList.add('dragover')
    }
    const onDragLeave = ()=>{
        wrapperRef.current.classList.remove('dragover')
    }
    const onDrop = ()=>{
        wrapperRef.current.classList.remove('dragover')
    }

    return (
        <>
            <div ref={wrapperRef} className='drop-file-input' onDragEnter={onDragEnter} onDragLeave={onDragLeave} onDrop={onDrop}>
                <div className='drop-file-input__label'>
                    <img src={uploadImage} alt=""/>
                    <p>
                        Drag & Drop your files here
                    </p>
                    <input type="file" value="" onChange={onFileDrop}/>
                    
                </div>
                <button className='submit-button' onClick={()=>{}}>submit</button>
        </div>
        {
            fileList.length > 0 ? (
                <div className="drop-file-preview">
                    <p className="drop-file-preview__title">
                        Ready to upload
                    </p>
                    {
                        fileList.map((item, index)=>(
                            <div key={index} className="drop-file-preview__item">
                                <img src={ImageConfig[item.type.split('/')[1]] || ImageConfig['default']} width="pixels" alt=""/>
                                <div className="drop-file-preview__item_info">
                                    <p>{item.name}</p>
                                    <p>{item.size}B</p>
                                    {/* {onImgLoad(item)} */}
                                </div>
                                <span className="drop-file-preview__item_del" onClick={()=>{
                                    fileRemove(item)
                                }}>
                                    X
                                </span>
                            </div>
                        ))
                    }
                </div>
            ) : null
        }
        </>
    );
}

DropFileInput.propTypes = {
    onFileChange:PropTypes.func
}

export default DropFileInput;
