import "../styles/Login.css"
import { useState, useEffect } from "react";

export default function ClientLogin(){

    const [usernameSignIn,setUsernameSignIn] = useState('')
    const [passwordSignIn,setPasswordSignIn] = useState('')

    const handleLogin = (ev) => {
        console.log(usernameSignIn, passwordSignIn)
    }

    useEffect(()=>{
        const toSignUpButton = document.getElementById('signUp');
        const backButton = document.getElementById('back');
        const container = document.getElementById('container');
        

        toSignUpButton.addEventListener('click', () => {
            container.classNameList.add("right-panel-active");
        });
        
        backButton.addEventListener('click', () => {
            container.classNameList.remove("right-panel-active");
        });
        
        // const signUpForm = document.getElementById('sign-up-form')
        // const si_password = document.getElementById('sign-in-password')
        // const si_password = document.getElementById('sign-in-password')
        // const si_password = document.getElementById('sign-in-password')

        
        // console.log("Test")
        // signInForm.addEventListener('submit', (event)=>{
        //     event.preventDefault()
        //     console.log(si_email.value)
        //     fetch('http://localhost:8080/login', {
        //         'method':'POST',
        //         'body': JSON.stringify(
        //             {
        //                 'username':si_email.value,
        //                 'password':si_password.value
        //         }),
        //         'headers': {
        //             'Content-Type': 'application/json'
        //             // 'Content-Type': 'application/x-www-form-urlencoded',
        //           }
        //     }).then((data)=>{
        //         console.log(data)
        //     }).catch((err)=>{
        //         console.log(err)
        //     })
        // })
    })
    



    return (
        <div className="container" id="container">
            <div className="form-container sign-up-container">
                <form action="#" id='sign-up-form'>
                    <h1>Create Account</h1>
                    <input type="text" placeholder="Name" id='sign-up-name'/>
                    <input type="email" placeholder="Email" id='sign-up-email'/>
                    <input type="password" placeholder="Password" id='sign-up-password'/>
                    <button className='sign-up-button' >Sign Up</button>
                </form>
            </div>
            <div className="form-container sign-in-container">
                <form  id='sign-in-form' onSubmit={handleLogin}>
                    <h1>Sign in</h1>
                    <input type="email" onChange={(event)=>{setUsernameSignIn(event.target.value);}} placeholder="Email" id='sign-in-email' />
                    <input type="password" onChange={(event)=>{setPasswordSignIn(event.target.value)}} placeholder="Password" id='sign-in-password' />
                    <a href="#">Forgot your password?</a>
                    <button className='sign-in-button'>Sign In</button>
                </form>
            </div>
            <div className="overlay-container">
                <div className="overlay">
                    <div className="overlay-panel overlay-left">
                        <h1>I've got an acoount already!</h1>
                        <p>To keep connected with us please login with your personal info</p>
                        <button className="ghost" id="back">back</button>
                    </div>
                    <div className="overlay-panel overlay-right">
                        <h1>Don't have account?</h1>
                        <p>Let's sign up to be a part of us for FREE!</p>
                        <button className="ghost" id="signUp">Sign Up</button>
                    </div>
                </div>
            </div>
        </div>

    );
}
